const apiKey = 'lg3lu2ut3YI1qcuAuvzQmsY-L_EWF9WuSqyjFw11YkRlHwyqNC1loB4LjJlCnoqldR7gakFf92zuLrIY_bKOSjsCS1_n_EMNYS_dtKhlOEm7gUSM0XkvZTssEYKXX3Yx'
const Yelp = {
    async search(term, location, sortBy) {
      const response = await fetch(`https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/search?term=${term}&location=${location}&sort_by=${sortBy}`,
      {
        headers: {
          Authorization: `Bearer ${apiKey}`
        }
      });
    const jsonResponse = await response.json();
    if (jsonResponse.businesses) {
      console.log(jsonResponse.businesses.map(business => ({
        id: business.id,
        imageSrc: business.image_url,
        name: business.name,
        address: business.location.address1,
        city: business.location.city,
        state: business.location.state,
        zipCode: business.location.zip_code,
        category: business.categories[0].title,
        rating: business.rating,
        reviewCount: business.review_count
      })));
    }
    }
  };

  export default Yelp
