import React from 'react';
import './App.css';
import SearchBar from '../SearchBar/SearchBar';
import BusinessList from '../Business-List/BusinessList';
import axios from 'axios';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      businesses: []
    };
  } 

  /*searchYelp(term, location, sortBy) {
    Yelp.search(term, location, sortBy).then(businesses => {
      this.setState({businesses: businesses});
    });
  }*/

  search = async (term, location, sort_by) => {
    try {
      const response = await axios.get( `/search/${term}/${location}/${sort_by}`)
      const businessRes = response.data.businesses
      if (businessRes) {
        return businessRes.map(business => ({
            id: business.id,
            imageSrc: business.image_url,
            name: business.name,
            address: business.location.address1,
            city: business.location.city,
            state: business.location.state,
            zipCode: business.location.zip_code,
            category: business.categories[0].title,
            rating: business.rating,
            reviewCount: business.review_count
        }))
      }
      console.log(businessRes)
      this.setState({businesses: response})
    } catch (error) {
      console.log(error.message)
    }

  }

  render() {
    return (
      <div className="App">
        <SearchBar 
          businesses={this.state.businesses} 
          search={this.search.bind(this)} />
        <BusinessList 
          businesses={this.state.businesses}/>
      </div>
    );
  }
}

export default App;
